import React from 'react';

const AnotherPage = ({ items = [] }) => {
    return (
        <div className='flex flex-col flex-wrap items-center md:flex-row'>
            <div className='flex justify-between gap-10 px-10 py-10'>
                {items.map((item, index) => (
                    <ul key={index} className='flex flex-wrap w-2/4 border border-red-100'>
                        <li className='p-4 flex'>
                            <img src='' alt={item.imgAlt || 'Product Image'} className='w-full h-fit object-cover' />
                            <p className='text-sm p-4'>{item.description}</p>
                        </li>
                        <h1 className='text-3xl font-mono font-bold mt-2'>{item.title}</h1>
                        <li className='flex justify-between w-full mt-4'>
                            <h1 className='text-lg'>{item.price}</h1>
                            <h1 className='text-lg'>{item.loop_just}</h1>
                        </li>
                        <div className='count flex mt-3 cursor-pointer'>
                            <h1 className='text-lg p-2'>-</h1>
                            <input className='border-gray-600 border w-fit px-10' type="number" />
                            <h1 className='text-lg p-2'>+</h1>
                        </div>
                        <button className='bg-yellow-300 rounded-md transition-transform duration-300 ease-in-out text-sm py-3 font-extrabold hover:bg-yellow-400 mt-2'>
                            Buy Now
                        </button>
                    </ul>
                ))}
            </div>
        </div>
    );
};

export const defultloop = [
    {
        _id: 1,
        tile: 'Products',
        description: ' Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsam debitis aperiam aliquid fugiat similique ex? Vel ipsam neque qui repellendus corrupti enim facilis dignissimos magni praesentium totam itaque, officiis libero Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsam debitis aperiam aliquid fugiat similique ex? Vel ipsam neque qui repellendus corrupti enim facilis dignissimos magni praesentium totam itaque, officiis libero',
        price: '$100',
        loop_just: 'xxxx',
        imgAlt: 'Product Image'
    },

]
export default AnotherPage;
