import React from 'react';

const AnotherPage = (props) => {
    const { items } = props;

    return (
        <div className='flex flex-col flex-wrap items-center md:flex-row'>
            <div className='flex flex-wrap gap-10 px-10 py-10'>
                {items.map((item, index) => (
                    <ul key={index} className='flex border border-red-100'>
                        <li className='p-4 w-full'>
                            <img src={item.imgSrc || ''} alt={item.imgAlt || ''} className='w-full h-fit object-cover' />
                            <div className='flex justify-between mt-4'>
                                <h1 className='text-lg'>{item.price}</h1>
                                <h1 className='text-lg'>{item.loop_just}</h1>
                            </div>
                            <div className='flex justify-between'>
                                <h1 className='text-3xl font-mono font-bold mt-2'>{item.title}</h1>
                                <p className='text-sm mt-2'>{item.description}</p>
                            </div>
                            <button className='bg-yellow-300 rounded-md transition-transform p-1 duration-300 ease-in-out text-sm py-3 font-extrabold hover:bg-yellow-400'>
                                Add to Cart
                            </button>
                            <button className='bg-yellow-300 rounded-md transition-transform duration-300 ease-in-out text-sm py-3 font-extrabold hover:bg-yellow-400 mt-2'>
                                Buy Now
                            </button>
                        </li>
                    </ul>
                ))}
            </div>
        </div>
    );
};

export default AnotherPage;

export const defaultItems = [
    {
        _id: 1,
        title: 'Products',
        description: 'Lorem ipsum dolor sit amet consectetur adipisicing elit. Ipsam debitis aperiam aliquid fugiat similique ex? Vel ipsam neque qui repellendus corrupti enim facilis dignissimos magni praesentium totam itaque, officiis libero.',
        price: '$100',
        loop_just: 'xxxx',
        imgSrc: '', // Add actual image source
        imgAlt: 'Product Image' // Add actual image alt text
    },
    // Add more items as needed
];

// Usage example
// <AnotherPage items={defaultItems} />
