import React, { useState, useEffect } from 'react';
import axios from 'axios';

const HomePage = () => {
    const [products, setProducts] = useState([]);
    const [error, setError] = useState(null);

    useEffect(() => {
        async function fetchProducts() {
            try {
                const { data } = await axios.get('http://127.0.0.1:8000/');
                setProducts(data);
            } catch (error) {
                setError(error.message);
            }
        }
        fetchProducts();
    }, []);

    return (
        <div className='flex flex-col flex-wrap justify-center items-center md:flex-row'>
            <div className='flex flex-wrap justify-center gap-10 px-10 py-10'>
                {error && <div className='text-red-500'>Error: {error}</div>}
                <ul className='flex flex-col flex-wrap justify-between items-center space-y-10 md:flex-row md:space-y-0 md:space-x-10'>
                    {products.map((product) => (
                        <li key={product.id} className='border border-red-100 p-4 w-full flex flex-col bott md:w-80'>
                            <img src={product.image} alt={product.name} className='w-full h-48 object-cover' />
                            <div className='flex justify-between mt-4'>
                                <h1 className='text-lg'>${product.price}</h1>
                                <h1 className='text-lg'>{product.loop_just}</h1>
                            </div>
                            <h1 className='text-3xl font-mono font-bold mt-2'>{product.name}</h1>
                            <p className='text-sm mt-2'>{product.description}</p>
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
}

export default HomePage;
